mapper.select:
select
count(ID)
from SYS_ROLE where ROLENAME = #{rolename,jdbcType=VARCHAR}
and ID != #{id,jdbcType=DECIMAL} and STATUS = 1 and ROLETYPE =
#{roletype,jdbcType=DECIMAL}

select r.ID, r.ROLENAME, r.REMARK, r.STATUS, r.ROLETYPE, r.CREATOR, r.INTIME, u.USERNAME as CREATORNAME from SYS_ROLE r left join SYS_USER u on u.ID = r.CREATOR where r.STATUS = 1
and r.ROLETYPE = #{roletype,jdbcType=DECIMAL}
and u.ORGANIZATIONCODE in (select code from view_organization Where FIND_IN_SET(code,getChildList(#{organizationcode,jdbcType=VARCHAR})) )
order by r.ID desc limit #{startindex,jdbcType=DECIMAL} , #{size,jdbcType=DECIMAL}

select count(0) from SYS_ROLE r left join SYS_USER u on u.ID = r.CREATOR where r.STATUS = 1
and r.ROLETYPE = #{roletype,jdbcType=DECIMAL}
and u.ORGANIZATIONCODE in (select code from view_organization Where FIND_IN_SET(code,getChildList(#{organizationcode,jdbcType=VARCHAR})) )

mapper.update:
update SYS_ROLE
set ROLENAME = #{rolename,jdbcType=VARCHAR},
REMARK = #{remark,jdbcType=VARCHAR},
STATUS = #{status,jdbcType=DECIMAL},
ROLETYPE = #{roletype,jdbcType=DECIMAL}
where ID = #{id,jdbcType=DECIMAL}
